import React, { useState, useEffect } from 'react';
import EventCard from '../components/EventCard';
import axios from 'axios';

const Home = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        // Sample data; replace this with actual API call when available
        const sampleEvents = [
            { id: 1, title: 'Concert Night', date: '2024-11-01', image: 'https://via.placeholder.com/300', description: 'Join us for an evening of great music!' },
            { id: 2, title: 'Tech Conference', date: '2024-12-05', image: 'https://via.placeholder.com/300', description: 'Explore the latest in technology and innovation.' },
            { id: 3, title: 'Art Exhibition', date: '2024-10-20', image: 'https://via.placeholder.com/300', description: 'Experience the finest art pieces from local artists.' }
        ];

        // Uncomment below to fetch from API
        // axios.get('/api/events')
        //     .then(response => setEvents(response.data))
        //     .catch(error => console.error(error));

        setEvents(sampleEvents);
    }, []);

    return (
        <div className="home">
            <h1>Upcoming Events</h1>
            <div className="event-list">
                {events.map(event => (
                    <EventCard key={event.id} event={event} />
                ))}
            </div>
        </div>
    );
};

export default Home;
