import React, { useState, useEffect } from 'react';
import EventCard from '../components/EventCard';
import './Home.css'; // Create this file for additional styles

const Home = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        // Sample data; replace this with actual API call when available
        const sampleEvents = [
            { id: 1, title: 'Concert Night', date: '2024-11-01', image: 'https://via.placeholder.com/300', description: 'Join us for an evening of great music!' },
            { id: 2, title: 'Tech Conference', date: '2024-12-05', image: 'https://via.placeholder.com/300', description: 'Explore the latest in technology and innovation.' },
            { id: 3, title: 'Art Exhibition', date: '2024-10-20', image: 'https://via.placeholder.com/300', description: 'Experience the finest art pieces from local artists.' }
        ];

        // Uncomment below to fetch from API
        // axios.get('/api/events')
        //     .then(response => setEvents(response.data))
        //     .catch(error => console.error(error));

        setEvents(sampleEvents);
    }, []);

    return (
        <div className="home">
            <h1>Welcome to Event Booking</h1>
            <p>Your one-stop solution for booking exciting events in your city. Discover concerts, workshops, exhibitions, and much more!</p>

            <h2>Featured Events</h2>
            <div className="event-list">
                {events.map(event => (
                    <EventCard key={event.id} event={event} />
                ))}
            </div>

            <h2>Why Choose Us?</h2>
            <p>We provide a seamless event booking experience with various events tailored to your interests. Our platform allows you to:</p>
            <ul>
                <li>Discover a wide range of events</li>
                <li>Book tickets effortlessly</li>
                <li>Get the latest updates on your favorite events</li>
            </ul>

            <h2>Testimonials</h2>
            <div className="testimonials">
                <blockquote>
                    "I had a fantastic experience booking my tickets through this platform! Highly recommend!"
                </blockquote>
                <cite>- Jane Doe</cite>
            </div>
        </div>
    );
};

export default Home;
